query:
{
  Solana {
    DEXTradeByTokens(
      limit: {count: 20}
      orderBy: {descending: Block_Time}
      where: {
        Trade: {
          Dex: { ProtocolName: { is: "Raydium" } },
          Currency: { MintAddress: { in: ["bioJ9JTqW62MLz7UKHU69gtKhPpGi1BQhccj2kmSvUJ", "GJtJuWD9qYcCkrwMBmtY1tpapV1sKfB2zUv9Q4aqpump"] } },
          Side: { Currency: { MintAddress: { in: ["bioJ9JTqW62MLz7UKHU69gtKhPpGi1BQhccj2kmSvUJ", "GJtJuWD9qYcCkrwMBmtY1tpapV1sKfB2zUv9Q4aqpump"] } } }
        }
      }
    ) {
      Block { Time Slot }
      Transaction { Signature }
      Trade {
        AmountInUSD
        PriceInUSD
        Currency { Symbol MintAddress }
        Side { Currency { Symbol MintAddress } }
        Market { MarketAddress }
        Dex { ProtocolName ProtocolFamily }
      }
    }
  }
}
