query:
{
  Solana {
    DEXTradeByTokens(
      limit: {count: 150}
      orderBy: {descending: Block_Time}
      where: {
        Trade: {
          Currency: { MintAddress: { is: "GJtJuWD9qYcCkrwMBmtY1tpapV1sKfB2zUv9Q4aqpump" } }
        }
      }
    ) {
      Block { Time Slot }
      Transaction { Signature }
      Trade {
        AmountInUSD
        PriceInUSD
        Currency { Symbol MintAddress }
        Side { Currency { Symbol MintAddress } }
        Market { MarketAddress }
        Dex { ProtocolName ProtocolFamily }
      }
    }
  }
}
