query:
{
  Solana {
    DEXTradeByTokens(
      limit: {count: 5}
      orderBy: {descending: Block_Time}
      where: {
        Trade: {
          Dex: { Name: { is: "Raydium" } }
          Currency: { MintAddress: { is: "vita3LfgKErA37DWA7W8RBks3c7Rym2hPFgizNRshBi" } }
          Side: { Currency: { MintAddress: { in: ["So11111111111111111111111111111111111111112", "EPjFWdd5AufqSSqeM2qN1xzybapC8G4wEGGkZwyTDt1v"] } } }
        }
      }
    ) {
      Block { Time Slot }
      Transaction { Signature }
      Trade {
        AmountInUSD
        PriceInUSD
        Currency { Symbol MintAddress }
        Side { Currency { Symbol MintAddress } }
        Market { MarketAddress }
        Dex { Name ProtocolName ProtocolFamily }
      }
    }
  }
}
